import React, {useState, useEffect} from 'react';

//Packages
import {Link} from 'react-router-dom';
import {FaShoppingBasket as NavLogo} from 'react-icons/fa'
import {RiShoppingCartLine as CartLogo} from 'react-icons/ri';

//Components
import Button from './../button/Button';

//CSS
import './Nav.css';

function Nav() {
    const [navClick, setNavClick] = useState(false);
    const [button, setButton] = useState(true);

    const onHandleClick = () => setNavClick(!navClick);
    const closeMobileMenu = () => setNavClick(false);

    const showButton = () => {
        if (window.innerWidth <= 960) {
            setButton(false)
        } else {
            setButton(true);
        }
    };

    useEffect(() => {
        showButton();
    }, []);

    window.addEventListener('resize', showButton)

    return (
        <>
            <div className="nav__top">
                <p className={'white'}>All of your favorites, in one place</p>
            </div>

            <nav className={'shadow-2'}>
                <div className={'container'}>

                    <div className="menu__icon" onClick={onHandleClick}>
                        <i className={navClick ? 'hi' : 'bye'} />
                    </div>

                    <ul>
                        <Link to={'/'} onClick={closeMobileMenu}>
                            <li className={'nav__logo-name'}>
                                <p className={'nav__logo-p'}>grcy</p>
                                <NavLogo className={'nav__logo-icon'}/></li>
                        </Link>
                    </ul>

                    <ul>

                        <Link to={'product-page'} onClick={closeMobileMenu}>
                            <li className={'search; center'}>
                                <input placeholder={'search'}/>
                            </li>
                        </Link>

                        <Link to={'login'} onClick={closeMobileMenu}>
                            <li className={'login'}>Login</li>
                        </Link>

                        <Link to={'checkout'} onClik={closeMobileMenu}>
                            <li className={'cart'}>
                                Cart
                                <CartLogo className={'cart__icon-logo'} />
                            </li>
                        </Link>

                    </ul>
                    {button && <Button buttonStyle='btn--outline'>SIGN UP</Button>}

                </div>
            </nav>
        </>
    )
}

export default Nav;
